    private void btnAceptarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAceptarActionPerformed

        String codigoIngresado = txtCodigoPartida.getText().trim();

        // Validar que se haya ingresado un código de partida
        if (codigoIngresado.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Por favor, ingresa un código de partida.", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }

        // Crear cliente y conectar con el servidor
        Cliente cliente = new Cliente("localhost", 50065);
        cliente.conectar();  // Conectamos al servidor

        // Usar el método unirseAPartida para unirse a la sala
        cliente.unirseAPartida(codigoIngresado);  // Enviar código de la partida al servidor

        // Recibir respuesta del servidor
        String respuesta = cliente.recibirMensaje();

        // Si la respuesta es exitosa, mostramos el tablero
        if (respuesta != null && respuesta.equals("Unido a la partida correctamente.")) {
            JOptionPane.showMessageDialog(this, "Unido a la partida correctamente.", "Éxito", JOptionPane.INFORMATION_MESSAGE);

            // Mostrar el tablero de juego
            FrmTablero frmTablero = FrmTablero.getInstance(); // Obtén la instancia del tablero
            frmTablero.setVisible(true);  // Hacer visible el tablero
            this.dispose();  // Cerrar la ventana actual de "Unirse a la partida"
        } else {
            JOptionPane.showMessageDialog(this, "El código ingresado es incorrecto o la partida ya está en curso.", "Error", JOptionPane.ERROR_MESSAGE);
        }

    }//GEN-LAST:event_btnAceptarActionPerformed

